import React, { useRef } from 'react'
import { useDispatch } from 'react-redux';
import { addContact, updateContact } from './redux/slices/contactSilce';
import './App.css';

function AddContact({text, info, prs}) {
  const dispatch = useDispatch();
  const phoneRef = useRef();
  const cityRef = useRef();
  const nameRef = useRef();

  const handleAddContacts =  (e) => {
    let color = `#${Math.floor(Math.random() * 0xffffff).toString(16)}`;
    let name = nameRef.current.value.trim();
    let tel = phoneRef.current.value.trim();
    let ville = cityRef.current.value.trim();
    if (name =='' || tel=='' || ville =='') {
      return;
    }
    dispatch(addContact({
      name,ville,tel,color
    }));
    phoneRef.current.value = '';
    nameRef.current.value = '';
    cityRef.current.value = '';
  }
  const handleUpdateContacts =  (e) => {
    let color = `#${Math.floor(Math.random() * 0xffffff).toString(16)}`;
    let name = nameRef.current.value.trim();
    let tel = phoneRef.current.value.trim();
    let ville = cityRef.current.value.trim();
    if (name =='' || tel=='' || ville =='') {
      return;
    }
    dispatch(updateContact({
      name,ville,tel,color
    }));
    phoneRef.current.value = '';
    nameRef.current.value = '';
    cityRef.current.value = '';
  }
  return (
    <div className="col-9 mx-auto mt-5 py-2 px-4">
      <div className="mb-3 row text-dark">
        <div className="col">
          <label className="form-label">Name</label>
          <input type="text" ref={nameRef} defaultValue={info != undefined ? info.name : ''} className="form-control shadow-none" placeholder="Name" aria-describedby="emailHelp" />
        </div>
      </div>
      <div className="mb-3 row text-dark">
        <div className="col">
          <label className="form-label">Phone Number</label>
          <input type="text" ref={phoneRef} defaultValue={info != undefined ? info.tel : ''} className="form-control shadow-none" placeholder="Phone Number" aria-describedby="emailHelp" />
        </div>
        <div className="col">
          <label className="form-label">City</label>
          <input type="text" ref={cityRef} defaultValue={info != undefined ? info.ville : ''} className="form-control shadow-none" placeholder="City" aria-describedby="emailHelp" />
        </div>
      </div>
      <div className="row">
        <button onClick={prs == "add" ? handleAddContacts : handleUpdateContacts} className='col-5 mx-auto btn mt-4 ' style={{ backgroundColor: '#F6EAD3' }}>
          {text}
        </button>
      </div>
    </div>
  )
}


export default AddContact;
