import { createSlice } from '@reduxjs/toolkit'


const randomuuid = crypto.randomUUID()


const contactSlice = createSlice({
    name: 'contacts',
    initialState: [{ randomuuid : {
        user: { id: randomuuid, avatar: `https://robohash.org/Gl.png?set=set3` },
        name: 'Aymane Chabat',
        tel: '0606060606',
        ville: "Tanger"
    }
    }],
    reducers: {
        addContact: (state, action) => {
            const { name, tel, ville } = action.payload
            const idd = crypto.randomUUID()
            state.push({ idd : {
                user: {
                    id: idd,
                    avatar: `https://robohash.org/${name}.png?set=set3`
                },
                name, tel, ville
            }
            })
        },
        updateContact: (state, action) => {
            const {id, name, tel, ville} = action.payload
            state[id] = {
                user: {
                    id: id,
                    avatar: `https://robohash.org/${name}.png?set=set3`
                },
                name, tel, ville
            }
        },
        deleteContact: (state, action) => {
            return state.filter(contact => contact.user.id != action.payload.id);
        }
    }
});

export const {
    addContact,
    deleteContact,
    filterContact ,
    updateContact
} = contactSlice.actions;
export default contactSlice.reducer;
