import { useState } from 'react';
import React from 'react';
import { Route, Routes } from 'react-router-dom';
import './App.css';
import Home from './Home';
import NavBar from './NavBar';
import AddContact from './AddContact';
import CContext from './AvatarContext';


function App() {
  const [contacts, setContacts] = useState();
  const [avatar, setAvatar] = useState('');
  return (
    <div className="App">
      <div className="container py-5">
        <div className="row app-wrapper ">
          <div className={`col-5 py-0 row `} style={{backgroundColor:`black`}}>
            <img src={`${avatar == '' ? '' : avatar.avatar}`} alt="" />
            <AddContact text={'Update Contact'}/>
          </div>
          <div className="col-7 contact-list">
            <NavBar />
            <CContext.Provider value={{ contacts, setContacts, avatar, setAvatar }}>
              <Routes>
                <Route path="/">
                  <Route index element={<Home />} />
                  <Route path='new-contact' element={<AddContact text={'Add Contact'}/>} />
                </Route>
              </Routes>
            </CContext.Provider>
          </div>
        </div>
      </div>
    </div>
  );
}
export default App;
