import { createSlice } from '@reduxjs/toolkit'



const contactSlice = createSlice({
    name: 'contacts',
    initialState: [{ 
        user: { id: crypto.randomUUID(), avatar: `https://robohash.org/Gl.png?set=set3` },
        name: 'Aymane Chabat',
        tel: '0606060606',
        ville: "Tanger"
    }],
    reducers: {
        addContact: (state, action) => {
            const { name, tel, ville } = action.payload
            state.push({
                user: {
                    id: crypto.randomUUID(),
                    avatar: `https://robohash.org/${name}.png?set=set3`
                },
                name, tel, ville
            })
        },
        updateContact: (state, action) => {
            const {id, name, tel, ville} = action.payload
            const index = state.findIndex(element => 
                element.user.id == id
            )
            console.log(id)
        },
        deleteContact: (state, action) => {
            return state.filter(contact => contact.user.id != action.payload.id);
        }
    }
});

export const {
    addContact,
    deleteContact,
    filterContact ,
    updateContact
} = contactSlice.actions;
export default contactSlice.reducer;
