import { useState } from 'react';
import React from 'react';
import { Route, Routes } from 'react-router-dom';
import './App.css';
import Home from './Home';
import NavBar from './NavBar';
import AddContact from './AddContact';
import CContext from './AvatarContext';
import UpdateContact from './updateContact'


function App() {
  const [contacts, setContacts] = useState();
  const [CContact, setCurrentContact] = useState('');
  return (
    <div className="App">
      <div className="container py-5">
        <div className="row app-wrapper ">
          <div className={`col-5 py-0 row`} style={{backgroundColor:`black`,display:'flex',flexDirection:'column',justifyContent:'center',alignItems:'center'}}>
            <img src={`${CContact == '' ? '' : CContact.user.avatar}`} style={{height:'150px',width:'170px'}} alt="" />
            <UpdateContact text={'Update Contact'} info={CContact}/>
          </div>
          <div className="col-7 contact-list">
            <NavBar />
            <CContext.Provider value={{ contacts, setContacts, CContact, setCurrentContact }}>
              <Routes>
                <Route path="/">
                  <Route index element={<Home />} />
                  <Route path='new-contact' element={<AddContact text={'Add Contact'} prs={'add'}/>} />
                </Route>
              </Routes>
            </CContext.Provider>
          </div>
        </div>
      </div>
    </div>
  );
}
export default App;
